import React, { useState, useEffect } from 'react';
import swal from 'sweetalert2';

const symbols = ['bicycle', 'bicycle', 'leaf', 'leaf', 'cube', 'cube', 'anchor', 'anchor', 'paper-plane-o', 'paper-plane-o', 'bolt', 'bolt', 'bomb', 'bomb', 'diamond', 'diamond'];

const MemoryGame = () => {
    const [cards, setCards] = useState([]);
    const [opened, setOpened] = useState([]);
    const [match, setMatch] = useState(0);
    const [moves, setMoves] = useState(0);
    const [clicks, setClicks] = useState(0);
    const [timer, setTimer] = useState(null);

    useEffect(() => {
        init();
    }, []);

    useEffect(() => {
        if (match === 8) {
            const score = setRating(moves).score;
            setTimeout(() => {
                endGame(moves, score);
            }, 500);
        }
    }, [match]);

    const gameTimer = () => {
        const startTime = new Date().getTime();
        const intervalId = setInterval(() => {
            const now = new Date().getTime();
            const elapsed = now - startTime;
            const minutes = Math.floor((elapsed % (1000 * 60 * 60)) / (1000 * 60));
            let seconds = Math.floor((elapsed % (1000 * 60)) / 1000);
            if (seconds < 10) {
                seconds = `0${seconds}`;
            }
            const currentTime = `${minutes}:${seconds}`;
            // Update clock on game screen and modal
            // Assuming you have a state variable for currentTime
            // e.g., const [currentTime, setCurrentTime] = useState('0:00');
            // setCurrentTime(currentTime);
        }, 750);
        setTimer(intervalId);
    };

    const init = () => {
        const shuffledCards = shuffle(symbols);
        setCards(shuffledCards);
        setMatch(0);
        setMoves(0);
        setClicks(0);
        clearInterval(timer);
        gameTimer();
    };

    const setRating = (moves) => {
        let score = 3;
        if (moves > 10 && moves <= 14) {
            score = 2;
        } else if (moves > 14) {
            score = 1;
        }
        return { score };
    };

    const endGame = (moves, score) => {
        const msg = score === 1 ? `${score} Star` : `${score} Stars`;
        swal({
            allowEscapeKey: false,
            allowOutsideClick: false,
            title: 'Congratulations! You Won!',
            text: `With ${moves} Moves and ${msg}\n Woooooo!`,
            type: 'success',
            confirmButtonColor: '#02ccba',
            confirmButtonText: 'Play again!'
        }).then((isConfirm) => {
            if (isConfirm) {
                init();
            }
        });
    };

    const onCardClick = (index) => {
        setClicks(clicks + 1);
        if (clicks === 0) {
            gameTimer();
        }
        if (opened.length > 1 || cards[index] === opened[0]) return;
        setOpened([...opened, cards[index]]);
        if (opened.length === 1) {
            setMoves(moves + 1);
            if (cards[index] === opened[0]) {
                setMatch(match + 1);
                setOpened([]);
            } else {
                setTimeout(() => {
                    setOpened([]);
                }, 800);
            }
        }
    };

    return (
        <div className="container">
            <h1>Memory Game</h1>
            <div id="score-panel">
                <div className="leftdiv">
                    <ul className="stars">
                        <li><i className="fa fa-star"></i></li>
                        <li><i className="fa fa-star"></i></li>
                        <li><i className="fa fa-star"></i></li>
                    </ul>
                    <span className="moves">{moves}</span> Moves
                </div>
                <div className="timers">
                    <article className="timer">
                        <h3 className="info">Time: <span className="clock">{timer}</span></h3>
                    </article>
                    <div className="restart">
                        <i className="fa fa-repeat"></i>
                    </div>
                </div>
                <div className="clear"></div>
            </div>
            <ul className="deck">
                {cards.map((symbol, index) => (
                    <li key={index} className={`card ${opened.includes(symbol) ? 'open' : ''}`} onClick={() => onCardClick(index)}>
                        <i className={`fa fa-${symbol}`}></i>
                    </li>
                ))}
            </ul>
        </div>
    );
};

const shuffle = (array) => {
    let index = array.length, temp, randomIndex;
    while (0 !== index) {
        randomIndex = Math.floor(Math.random() * index);
        index -= 1;
        temp = array[index];
        array[index] = array[randomIndex];
        array[randomIndex] = temp;
    }
    return array;
}

export default MemoryGame;
