import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { Link } from 'react-router-dom';
import PuzzleImage from '../assets/img/puzzle.jpg';
import ChatBot from '../assets/img/bot.jpg';
import Yoga from '../assets/img/yoga.jpg';
import Depression from '../assets/img/depression.jpg';
import Game from './Game';

const HomeContent = ({ activeButton }) => {
    const [gameClicked, setGameClicked] = useState(false);

    const handleGameClick = () => {
        setGameClicked(true);
    };

    const handleBackClick = () => {
        setGameClicked(false);
    };

    const slideData = [
        {
            src: PuzzleImage,
            title: 'Game',
            copy: 'Click to play the game',
            link: null // No link for the game
        },
        {
            src: ChatBot,
            title: 'ChatBot',
            copy: 'Chat with our virtual assistant',
            link: '/chat'
        },
        {
            src: Yoga,
            title: 'Yoga',
            copy: 'Explore our Yoga sessions',
            link: '/yoga'
        },
        {
            src: Depression,
            title: 'Depression',
            copy: 'Learn about coping with depression',
            link: '/depression'
        }
    ];

    return (
        <div className="container">
            <Carousel autoPlay infiniteLoop showThumbs={false}>
                {slideData.map((slide, index) => (
                    <div key={index}>
                        <img src={slide.src} alt={slide.title} />
                        <div className="carousel-caption">
                            <h3>{slide.title}</h3>
                            <p>{slide.copy}</p>
                            {slide.link && <Link to={slide.link}>Learn more</Link>}
                            {slide.title === 'Game' && (
                                <span onClick={handleGameClick} style={{ cursor: 'pointer' }}>Play Game</span>
                            )}
                        </div>
                    </div>
                ))}
            </Carousel>
            {gameClicked && (
                <div>
                    <h2 style={{ color: 'white', fontWeight: 'bold' }}>Puzzle Game</h2>
                    <Game />
                    <button onClick={handleBackClick}>Back</button>
                </div>
            )}
        </div>
    );
};

export default HomeContent;
