import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Preloader from './Preloader';
import '../assets/css/LoR.css';

const LorR = () => {
  const [tab, setTab] = useState('sign-in');
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    age: '',
    gender: 'male',
    familyBackground: 'rich',
    goals: '',
    currentStatus: '',
    phone: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/register', formData, { withCredentials: true });
      const res = response.data;
      if (res.status === true) {
        navigate('/welcome');
      } else {
        alert(res.message);
        setFormData({
          name: '',
          email: '',
          password: '',
          age: '',
          gender: 'male',
          familyBackground: 'rich',
          goals: '',
          currentStatus: '',
          phone: '',
        });
      }
    } catch (ex) {
      console.log(ex);
    }
  };

  const handleLoginSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/login', { ...formData }, { withCredentials: true });
      const res = response.data;
      if (res.status === true) {
        navigate(`/home/${res.user._id}`);
      } else {
        alert(res.message);
        setFormData({ ...formData, password: '' });
      }
    } catch (ex) {
      console.log(ex);
    }
  };

  return (
    <div>
      <Preloader />
      <div className="login-wrap">
        <div className="login-html">
          <input
            id="tab-1"
            type="radio"
            name="tab"
            value="sign-in"
            className="sign-in"
            checked={tab === 'sign-in'}
            onChange={() => setTab('sign-in')}
          />
          <label htmlFor="tab-1" className="tab">
            Sign In
          </label>
          <input
            id="tab-2"
            type="radio"
            name="tab"
            value="sign-up"
            className="sign-up"
            checked={tab === 'sign-up'}
            onChange={() => setTab('sign-up')}
          />
          <label htmlFor="tab-2" className="tab">
            Sign Up
          </label>
          <div className="login-form">
            <div className={`sign-in-htm ${tab === 'sign-in' ? 'active' : ''}`}>
              <h2>Login</h2>
              <form onSubmit={handleLoginSubmit}>
                {/* Login form fields */}
              </form>
            </div>
            <div className={`sign-up-htm ${tab === 'sign-up' ? 'active' : ''}`}>
              <h2>Register</h2>
              <form onSubmit={handleSubmit}>
                {/* Register form fields */}
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LorR;
