import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css';
import { useLocation, useNavigate } from 'react-router-dom';
import Profile from '../assets/img/profile-user.png';
import Logo from '../assets/img/carecube.png';
import { Carousel } from 'react-bootstrap'; // Import Carousel from react-bootstrap

const Home = () => {
  const [user, setUser] = useState(null);
  const userId = (useLocation().pathname).split('/')[2];
  const [activeButton, setActiveButton] = useState("homecontent");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const user = await axios.post('http://localhost:4000/getUser', {
          userId: userId
        });
        setUser(user.data.detail);
      } catch (err) {
        console.log(err);
      }
    }
    fetchUser();
  }, [userId]);

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
    if (buttonName === "logout") {
      navigate("/login");
    }
  };

  return (
    <div className="app-container">
      <div className="app-header">
        <div className='app-header-left'>
          <img src={Logo} alt="Logo" />
        </div>
        <div className='app-header-right'>
          <img src={Profile} alt="Profile" />
          <div>{user && user.username}</div>
        </div>
      </div>

      <div className="app-content">
        <div className="app-sidebar">
          <button
            className={activeButton === "homecontent" ? "active" : ""}
            onClick={() => handleButtonClick("homecontent")}
          >
            Home
          </button>
          <button
            className={activeButton === "councelling" ? "active" : ""}
            onClick={() => handleButtonClick("councelling")}
          >
            Counselling
          </button>
          <button
            className={activeButton === "analysis" ? "active" : ""}
            onClick={() => handleButtonClick("analysis")}
          >
            Analysis
          </button>
          <button
            className={activeButton === "aboutus" ? "active" : ""}
            onClick={() => handleButtonClick("aboutus")}
          >
            About Us
          </button>
        </div>
        <div className="projects-section">
          <div className="projects-section-header">
            <p></p>
            <p className="time"></p>
          </div>

          <Carousel interval={2000} indicators={false} controls={true} pause={false}>
            <Carousel.Item>
              <img src="la.jpg" alt="Los Angeles" style={{ width: '100%', height: 'auto' }} />
            </Carousel.Item>
            <Carousel.Item>
              <img src="chicago.jpg" alt="Chicago" style={{ width: '100%', height: 'auto' }} />
            </Carousel.Item>
            <Carousel.Item>
              <img src="ny.jpg" alt="New York" style={{ width: '100%', height: 'auto' }} />
            </Carousel.Item>
          </Carousel>
        </div>
      </div>
    </div>
  );
}

export default Home;
