import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Carousel.css";

const Carousel = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [image1, setImage1] = useState("");
  const [image2, setImage2] = useState("");
  const [image3, setImage3] = useState("");
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const fetchUser = async () => {
      const response = await axios.post("http://localhost:4000/getUser", {
        userId: userId,
      });
      setUser(response.data.detail);
    };

    fetchUser();
  }, [userId]);

  useEffect(() => {
    if (user) {
      setImage1(user.image1);
      setImage2(user.image2);
      setImage3(user.image3);
    }
  }, [user]);

  const handlePrevClick = () => {
    setActiveIndex((prevIndex) => (prevIndex === 0 ? 2 : prevIndex - 1));
  };

  const handleNextClick = () => {
    setActiveIndex((prevIndex) => (prevIndex === 2 ? 0 : prevIndex + 1));
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="carousel">
      <div className="carousel-inner">
        <div
          className={`carousel-item ${activeIndex === 0 ? "active" : ""}`}
          style={{ backgroundImage: `url(${image1})` }}
        >
          <div className="carousel-caption d-none d-md-block">
            <h2>{user.title1}</h2>
            <p>{user.description1}</p>
          </div>
        </div>
        <div
          className={`carousel-item ${activeIndex ===1 ? "active" : ""}`}
          style={{ backgroundImage: `url(${image2})` }}
        >
          <div className="carousel-caption d-none d-md-block">
            <h2>{user.title2}</h2>
            <p>{user.description2}</p>
          </div>
        </div>
        <div
          className={`carousel-item ${activeIndex === 2 ? "active" : ""}`}
          style={{ backgroundImage: `url(${image3})` }}
        >
          <div className="carousel-caption d-none d-md-block">
            <h2>{user.title3}</h2>
            <p>{user.description3}</p>
          </div>
        </div>
      </div>
      <button
        className="carousel-control-prev"
        type="button"
        data-bs-target="#carouselExampleIndicators"
        data-bs-slide="prev"
        onClick={handlePrevClick}
      >
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Previous</span>
      </button>
      <button
        className="carousel-control-next"
        type="button"
        data-bs-target="#carouselExampleIndicators"
        data-bs-slide="next"
        onClick={handleNextClick}
      >
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
};

export default Carousel;
