import React,{useState} from 'react'
import { Link, useNavigate } from "react-router-dom";
import axios from 'axios'
import Preloader from './Preloader';

const RegisCouncelling = () => {
    const navigate = useNavigate();
    const [values, setValues] = useState({ cause: "", howlong: "" });
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
          const response = await axios.post(
            "http://localhost:4000/regisCouncelling",
            {
              ...values,
            },
            { withCredentials: true }
          );    
          const res = response.data;
            if(res.status===true){
                navigate('/home')
            }else{
                alert(res.message);
                setValues({cause:"",howlong:""})
            }
        } catch (ex) {
          console.log(ex);
        }
    }
  return (
    <div className="container" style={{ height: "600px", display: "flex", justifyContent: "center", alignItems: "center" }}>
  <div className="content" style={{ padding: "40px", border: "1px solid #ccc", borderRadius: "5px", boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)" }}>
    <h2 style={{ textAlign: "center" }}>Register for Counseling</h2>
    <form onSubmit={(e) => handleSubmit(e)} style={{ display: "flex", flexDirection: "column" }}>
      <div className='input-group' style={{ marginBottom: "20px" }}>
        <label htmlFor="cause">Cause</label>
        <textarea
          type="cause"
          name="cause"
          onChange={(e) => setValues({ ...values, [e.target.name]: e.target.value })}
          style={{ width: "100%", padding: "10px", borderRadius: "5px", border: "1px solid #ccc", resize: "none" }}
        />
      </div>
      <div className='input-group' style={{ marginBottom: "20px" }}>
        <label htmlFor="howlong">How Long?</label>
        <textarea
          type="howlong"
          name="howlong"
          onChange={(e) => setValues({ ...values, [e.target.name]: e.target.value })}
          style={{ width: "100%", padding: "10px", borderRadius: "5px", border: "1px solid #ccc", resize: "none" }}
        />
      </div>
      <button type="submit" style={{ backgroundColor: "#007bff", color: "#fff", border: "none", padding: "10px 20px", borderRadius: "5px", cursor: "pointer" }}>Submit</button>
    </form>
  </div>
</div>

  )
}

export default RegisCouncelling
