import React, { useState, useEffect } from 'react';
import swal from 'sweetalert2';

const symbols = ['bicycle', 'bicycle', 'leaf', 'leaf', 'cube', 'cube', 'anchor', 'anchor', 'paper-plane-o', 'paper-plane-o', 'bolt', 'bolt', 'bomb', 'bomb', 'diamond', 'diamond'];

const shuffle = array => {
  let currentIndex = array.length, temporaryValue, randomIndex;
  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
}

const Card = ({ symbol, onClick }) => (
  <li className="card" onClick={onClick}>
    <i className={`fa fa-${symbol}`}></i>
  </li>
);

const Game = () => {
  const [cards, setCards] = useState(shuffle(symbols));
  const [opened, setOpened] = useState([]);
  const [match, setMatch] = useState(0);
  const [moves, setMoves] = useState(0);
  const [clicks, setClicks] = useState(0);
  const [currentTime, setCurrentTime] = useState('0:00');

  useEffect(() => {
    if (match === 8) {
      endGame();
    }
  }, [match]);

  const endGame = () => {
    const score = moves <= 10 ? 3 : moves <= 14 ? 2 : 1;
    swal({
      allowEscapeKey: false,
      allowOutsideClick: false,
      title: 'Congratulations! You Won!',
      text: `With ${moves} Moves and ${score} Stars\n Woooooo!`,
      type: 'success',
      confirmButtonColor: '#02ccba',
      confirmButtonText: 'Play again!'
    }).then((isConfirm) => {
      if (isConfirm) {
        setClicks(0);
        setMatch(0);
        setMoves(0);
        setCards(shuffle(symbols));
        clearInterval(timerInterval);
        setCurrentTime('0:00');
      }
    });
  }

  const handleCardClick = (index, symbol) => {
    setClicks(prevClicks => prevClicks + 1);
    if (clicks === 0) {
      gameTimer();
    }

    if (opened.length >= 2 || opened.includes(index)) return;

    setOpened(prevOpened => [...prevOpened, index]);

    if (opened.length === 1) {
      setMoves(prevMoves => prevMoves + 1);
      if (symbol === symbols[opened[0]]) {
        setTimeout(() => {
          setMatch(prevMatch => prevMatch + 1);
          setOpened([]);
        }, 800);
      } else {
        setTimeout(() => {
          setOpened([]);
        }, 800);
      }
    }
  }

  let timerInterval;

  const gameTimer = () => {
    let startTime = new Date().getTime();

    // Update the timer every second
    timerInterval = setInterval(() => {
      let now = new Date().getTime();
      let elapsed = now - startTime;
      let minutes = Math.floor((elapsed % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((elapsed % (1000 * 60)) / 1000);
      if (seconds < 10) {
        seconds = "0" + seconds;
      }
      let currentTime = minutes + ':' + seconds;
      setCurrentTime(currentTime);
    }, 1000);
  };

  return (
    <div className="containerdiv">
      <h1>Memory Game</h1>
      <div id="score-panel">
        <div className="leftdiv">
          <ul className="stars">
            <li><i className="fa fa-star"></i></li>
            <li><i className="fa fa-star"></i></li>
            <li><i className="fa fa-star"></i></li>
          </ul>
          <span className="moves">{moves}</span> Moves
        </div>
        <div className="timers">
          <article className="timer">
            <h3 className="info">Time: <span className="clock">{currentTime}</span></h3>
          </article>
          <div className="restart" onClick={() => endGame()}>
            <i className="fa fa-repeat"></i>
          </div>
        </div>
        <div className="clear"></div>
      </div>
      <ul className="deck">
        {cards.map((symbol, index) => (
          <Card
            key={index}
            symbol={symbol}
            onClick={() => handleCardClick(index, symbol)}
          />
        ))}
      </ul>
    </div>
  );
};

export default Game;
