import React, { useState, useEffect } from 'react';
import Card from './card';
import swal from 'sweetalert2';

const symbols = ['bicycle', 'bicycle', 'leaf', 'leaf', 'cube', 'cube', 'anchor', 'anchor', 'paper-plane-o', 'paper-plane-o', 'bolt', 'bolt', 'bomb', 'bomb', 'diamond', 'diamond'];

const Game = () => {
  const [opened, setOpened] = useState([]);
  const [match, setMatch] = useState(0);
  const [moves, setMoves] = useState(0);
  const [clicks, setClicks] = useState(0);
  const [timer, setTimer] = useState(null);

  useEffect(() => {
    init();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    if (match === 8) {
      endGame();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [match]);

  const gameTimer = () => {
    let startTime = new Date().getTime();

    // Update the timer every second
    const intervalId = setInterval(() => {
      let now = new Date().getTime();
      // Find the time elapsed between now and start
      let elapsed = now - startTime;
      // Calculate minutes and seconds
      let minutes = Math.floor((elapsed % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((elapsed % (1000 * 60)) / 1000);
      // Add starting 0 if seconds < 10
      if (seconds < 10) {
        seconds = "0" + seconds;
      }
      let currentTime = minutes + ':' + seconds;
      // Update clock on game screen and modal
      document.querySelector(".clock").innerText = currentTime;
    }, 750);

    // Store the intervalId in the timer state variable
    setTimer(intervalId);
  };

  const init = () => {
    let cards = shuffle(symbols);
    setOpened([]);
    setMatch(0);
    setMoves(0);
    setClicks(0);
    document.querySelector('.moves').innerText = '0';
    const stars = document.querySelectorAll('.fa-star-o');
    stars.forEach(star => star.classList.remove('fa-star-o'));
    stars.forEach(star => star.classList.add('fa-star'));

    let deck = document.querySelector('.deck');
    deck.innerHTML = '';
    cards.forEach(symbol => {
      let li = document.createElement('li');
      li.classList.add('card');
      li.innerHTML = `<i class="fa fa-${symbol}"></i>`;
      deck.appendChild(li);
    });

    addClickListener();
    document.querySelector(".clock").innerText = "0:00";
  };

  const shuffle = (array) => {
    let index = array.length, temp, randomIndex;
    while (0 !== index) {
      randomIndex = Math.floor(Math.random() * index);
      index -= 1;
      temp = array[index];
      array[index] = array[randomIndex];
      array[randomIndex] = temp;
    }
    return array;
  }

  const setRating = (moves) => {
    let score = 3;
    if (moves <= 10) {
      document.querySelectorAll('.stars li')[2].classList.remove('fa-star');
      document.querySelectorAll('.stars li')[2].classList.add('fa-star-o');
    } else if (moves > 10 && moves <= 14) {
      document.querySelectorAll('.stars li')[1].classList.remove('fa-star');
      document.querySelectorAll('.stars li')[1].classList.add('fa-star-o');
      score = 2;
    } else if (moves > 14) {
      document.querySelectorAll('.stars li')[0].classList.remove('fa-star');
      document.querySelectorAll('.stars li')[0].classList.add('fa-star-o');
      score = 1;
    }
    return score;
  };

  const endGame = () => {
    let score = setRating(moves);
    let msg = score === 1 ? score + ' Star' : score + ' Stars';
    swal({
      allowEscapeKey: false,
      allowOutsideClick: false,
      title: 'Congratulations! You Won!',
      text: 'With ' + moves + ' Moves and ' + msg + '\n Woooooo!',
      type: 'success',
      confirmButtonColor: '#02ccba',
      confirmButtonText: 'Play again!'
    }).then((isConfirm) => {
      if (isConfirm) {
        setClicks(0);
        clearInterval(timer);
        init();
      }
    });
  }

  const addClickListener = () => {
    let deck = document.querySelector('.deck');
    deck.querySelectorAll('.card:not(.match):not(.open)').forEach(card => {
      card.addEventListener('click', function () {
        setClicks(clicks + 1);
        clicks === 0 ? gameTimer() : '';
        if (document.querySelectorAll('.show').length > 1) { return true; };
        let currentCard = this;
        let symbol = currentCard.innerHTML;
        if (currentCard.classList.contains('open')) { return true; };
        currentCard.classList.add('open', 'show');
        opened.push(symbol);
        if (opened.length > 1) {
          if (opened[0] === opened[1]) {
            deck.querySelectorAll('.open').forEach(card => {
              card.classList.add('match', 'animated', 'infinite', 'rubberBand');
              setTimeout(() => {
                deck.querySelectorAll('.match').forEach(card => {
                  card.classList.remove('open', 'show', 'animated', 'infinite', 'rubberBand');
                });
              }, 800);
            });
            setMatch(match + 1);
          } else {
            deck.querySelectorAll('.open').forEach(card => {
              card.classList.add('notmatch', 'animated', 'infinite', 'wobble');
              setTimeout(() => {
                deck.querySelectorAll('.open').forEach(card => {
                  card.classList.remove('animated', 'infinite', 'wobble');
                });
              }, 800 / 1.5);
              setTimeout(() => {
                deck.querySelectorAll('.open').forEach(card => {
                  card.classList.remove('open', 'show', 'notmatch', 'animated', 'infinite', 'wobble');
                });
              }, 800);
            });
          }
          opened = [];
          setMoves(moves + 1);
          setRating(moves);
          document.querySelector('.moves').innerText = moves;
        }

        if (match === 8) {
          setRating(moves);
          let score = setRating(moves);
          setTimeout(() => {
            endGame(moves, score);
          }, 500);
        }
      });
    });
  };

  const restartGame = () => {
    swal({
      allowEscapeKey: false,
      allowOutsideClick: false,
      title: 'Are you sure?',
      text: "Your progress will be Lost!",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#02ccba',
      cancelButtonColor: '#f95c3c',
      confirmButtonText: 'Yes, Restart Game!'
    }).then((isConfirm) => {
      if (isConfirm) {
        setClicks(0);
        clearInterval(timer);
        init();
      }
    });
  };

  return (
    <div className="containerdiv">
      <h1>Memory Game</h1>
      <div id="score-panel">
        <div className="leftdiv">
          <ul className="stars">
            <li><i className="fa fa-star"></i></li>
            <li><i className="fa fa-star"></i></li>
            <li><i className="fa fa-star"></i></li>
          </ul>
          <span className="moves">0</span> Moves
        </div>
        <div className="timers">
          <article className="timer">
            <h3 className="info">Time: <span className="clock data">0:00</span></h3>
          </article>
          <div className="restart" onClick={restartGame}>
            <i className="fa fa-repeat"></i>
          </div>
        </div>
        <div className="clear"></div>
      </div>
      <ul className="deck">
        {symbols.map((symbol, index) => (
          <Card key={index} symbol={symbol} onClick={() => { }} />
        ))}
      </ul>
    </div>
  );
};

export default Game;
