import React, { useState, useEffect } from "react";
import axios from "axios";
import { Carousel } from "react-bootstrap";

const CarouselComponent = ({ userId }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const response = await axios.post("http://localhost:4000/getUser", {
        userId: userId,
      });
      setUser(response.data.detail);
    };

    fetchUser();
  }, [userId]);

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <Carousel>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src={user.image1}
          alt={user.title1}
        />
        <Carousel.Caption>
          <h3>{user.title1}</h3>
          <p>{user.description1}</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src={user.image2}
          alt={user.title2}
        />
        <Carousel.Caption>
          <h3>{user.title2}</h3>
          <p>{user.description2}</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src={user.image3}
          alt={user.title3}
        />
        <Carousel.Caption>
          <h3>{user.title3}</h3>
          <p>{user.description3}</p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
};

export default CarouselComponent
