import React, { useState } from 'react';
import Game from './Game';
import Preloader from './Preloader';
import { Link } from 'react-router-dom';

const HomeContent = ({ activeButton, images }) => {
  const [gameClicked, setGameClicked] = useState(false);

  return (
    <>
      <Preloader />
      {!gameClicked && (
        <>
          {images.map((image, index) => (
            <div className="project-box-wrapper" key={index}>
              <div className="project-box" style={{ backgroundColor: '#ffff' }}>
                <img src={image.src} style={{ width: '100%', height: '80%' }} alt={`Slide ${index + 1}`} />
                <div className="project-box-footer">
                  <div className="days-left" style={{ color: '#df3670' }}>
                    <span onClick={() => setGameClicked(index === 0 ? true : false)} style={{ cursor: 'pointer' }}>
                      {image.title}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </>
      )}
      {gameClicked && (
        <>
          <h2 style={{ color: 'white', fontWeight: 'bold' }}>Puzzle Game</h2>
          <Game />
          <button
            onClick={() => setGameClicked(false)}
            style={{ backgroundColor: 'white', color: '#007bff', fontSize: '30px', fontWeight: 'bold' }}
          >
            Back
          </button>
        </>
      )}
    </>
  );
};

export default HomeContent;
